// Valid Types: mastercard, visa, amex, dinersclub, enroute, discover, jcb, unknown, all (overrides all other settings)
jQuery.validator.addMethod("creditcardtypes", function(value, element, param) {
  if (/[^0-9\-]+/.test(value)) {
    return false;
  }

  value = value.replace(/\D/g, "");

  var validTypes = 0x0000;

  if (param.mastercard) {
    validTypes |= 0x0001;
  }
  if (param.visa) {
    validTypes |= 0x0002;
  }
  if (param.amex) {
    validTypes |= 0x0004;
  }
  if (param.dinersclub) {
    validTypes |= 0x0008;
  }
  if (param.enroute) {
    validTypes |= 0x0010;
  }
  if (param.discover) {
    validTypes |= 0x0020;
  }
  if (param.jcb) {
    validTypes |= 0x0040;
  }
  if (param.unknown) {
    validTypes |= 0x0080;
  }
  if (param.all) {
    validTypes = 0x0001 | 0x0002 | 0x0004 | 0x0008 | 0x0010 | 0x0020 | 0x0040 | 0x0080;
  }
  if (validTypes & 0x0001 && /^(5[12345])/.test(value)) { //mastercard
    return value.length === 16;
  }
  if (validTypes & 0x0002 && /^(4)/.test(value)) { //visa
    return value.length === 16;
  }
  if (validTypes & 0x0004 && /^(3[47])/.test(value)) { //amex
    return value.length === 15;
  }
  if (validTypes & 0x0008 && /^(3(0[012345]|[68]))/.test(value)) { //dinersclub
    return value.length === 14;
  }
  if (validTypes & 0x0010 && /^(2(014|149))/.test(value)) { //enroute
    return value.length === 15;
  }
  if (validTypes & 0x0020 && /^(6011)/.test(value)) { //discover
    return value.length === 16;
  }
  if (validTypes & 0x0040 && /^(3)/.test(value)) { //jcb
    return value.length === 16;
  }
  if (validTypes & 0x0040 && /^(2131|1800)/.test(value)) { //jcb
    return value.length === 15;
  }
  if (validTypes & 0x0080) { //unknown
    return true;
  }
  return false;
}, "Please enter a valid credit card number.");

jQuery.validator.addMethod("creditcardexpirymonth", function(value, element, param) {
  var today = new Date();
  var month = today.getMonth() + 1;
  var year = today.getFullYear() - 2000;
  
  var input_month = parseInt( $("#cc_expiry_month").val(), 10);
  var input_year = parseInt( $("#cc_expiry_year").val(), 10);

  return (input_month >= month && input_year >= year) || input_year > year;

}, "Please enter a valid expiry month.");

jQuery.validator.addMethod("creditcardexpiryyear", function(value, element, param) {
  var today = new Date();
  var year = today.getFullYear() - 2000;
  
  var input_year = parseInt( $("#cc_expiry_year").val(), 10);
  
  // trigger evaluation of the month field so we can check if 
  // combination of month and year is valid. this has to run assync so it doesn't 
  // crashes the validator ability to check all fields
  setTimeout(function(){ $(".complete_checkout").validate().element('#cc_expiry_month'); }, 1000);
  
  return input_year >= year;

}, "Please enter a valid expiry year.");
