function calc_subtotal(with_activities) {
  with_activities = with_activities || false;
  var t=0;
  $('.buy_area select.price-select').each(function(){
    var price = $(this).find('option[value='+$(this).val()+']').data('price')
    $(this).closest('tr.item_info').data('price', price);
  });
  $('.buy_area tr.item_info, .buy_area .item_info').each(function(){
    qty = parseInt($(this).find('.qty').val())
    t = t + ( $(this).data('price') * qty );
    if (with_activities) {
      // Activities by item
      $(this).next('.item_activities').find('.activities li input:checked').each(function() {
        t = t + ( $(this).parent().data('price') * qty );
      });
      // Global activities
      $('.product_activities').find('.activities li input:checked').each(function() {
        t = t + ( $(this).parent().data('price') * qty );
      }); 
    }
  });

  return t;
}

function item_quantity(item) {
  q = $('.item_info[data-item-id="'+item+'"]').find('.qty');
  return (parseInt(q.val()) * q.closest('.item_info').data('for-n-users'));
}

function total_quantity() {
	var q=0;
	$('.buy_area .qty').each(function(){
		q = q + (parseInt($(this).val()) * $(this).closest('.item_info').data('for-n-users'));
	});
	return q;
}

function activity_claimed_quantity(activity_dci, item) {
  var q=0;
  $('.buy_area .qty').each(function(){
    if ($(this).closest('.item_info[data-item-id!="'+item+'"]').next('.item_activities').find('[data-dci="'+activity_dci+'"] input:checked').length > 0) {
      q = q + (parseInt($(this).val()) * $(this).closest('.item_info').data('for-n-users'));
    }
  });
  q = q + item_quantity(item);

  return q;
}

