var currentPriceRequest = null;

function get_sw_subtotal() {
  form = $('#edit_cart, #new_cart').first().serializeArray();
  currentPriceRequest = $.ajax({
    url: "/cart_items/get_items_price",
    type: 'GET',
    dataType: 'json',
    data: form,
    beforeSend : function() {           
      if(currentPriceRequest != null) {
        currentPriceRequest.abort();
      }
    },
    success: function(data) {
      var subtotal = calc_subtotal(true);
      if (data.price != subtotal)
        $('.running_total_without_discount').text(subtotal).formatCurrency();
      $('.running_total').text("$"+data.price);
    },
    error: function(data) {
      console.log('Failed to get items price');
    },
    complete: function() {
      $('.running_total').removeClass('loading');
    }
  });
}

function should_get_sw_subtotal() {
  checked_items_activities = false; 
  $('.item_activities').each(function(){
    item = $(this).data('item-id');
    if (item_quantity(item) > 0 && $(this).find('input:checked').length > 0) {
      checked_items_activities = true;
    }
  })

  checked_product_activities = ($('.product_activities input:checked').length > 0 && total_quantity() > 0);

  return checked_items_activities || checked_product_activities;
}

