function show_available_activities(date, item, dci_codes) {

  item = item || false;
  dci_codes = dci_codes || false;
  
  lock_buy_area();

  $(".loading_activities_error, .checking_availability_error").addClass('hide');

  $('#product_buy_area div.running_total_without_discount').text('');

  if ( total_quantity() == 0 ) {
    hide_activities($('.activities'));

    if ($('#activity_date').val() != "") {
      unlock_buy_area(false);
    }

  } else {

    if (should_get_sw_subtotal()) {
      $('#product_buy_area div.running_total').text('').addClass('loading');
    }

    var product_id = $('#product_buy_area').data('product-id');
    lock_buy_area();

    var url = '/products/'+product_id+'/max_available?date='+date;
  
    if (item) {
      var item_activities = $('[data-item-id="'+item+'"]').next('.item_activities').find('.activities');
      if (item_quantity(item) == 0 ) {
        hide_activities(item_activities);
      } else {
        if (!item_activities.hasClass('open')) {
          console.log(item_activities);
          item_activities.prev('.loading_activities').removeClass('hide');
        }
      }

      url += '&item_id=' + item;
    }

    if (dci_codes.length > 0) url += '&dci_codes=' + dci_codes.join(',');

    $.ajax({
      url: url,
      type: 'GET',
      dataType: 'json',
      data: '',
      success: function(data) {
        unlock_buy_area(false)
        activities = null;
        if (data !== null) {
          if (item) {
            for (var i=0; i<data.length; i++) {
              // Current item activities
              activity = $('.item_activities[data-item-id='+item+'] li[data-dci="'+data[i].dci+'"]');
              if (item_quantity(item) > 0) {
                claimed_qty = activity_claimed_quantity(activity.data('dci'), item);
                if (data[i].qty_rem >= claimed_qty && data[i].qty_rem != 0) { 
                  activity.addClass('visible').slideDown();
                } else {
                  activity.find('input').prop('checked',false);
                  activity.removeClass('visible').slideUp();
                }              
              }

              // Rest of items activities
              $('.item_activities[data-item-id!="'+item+'"]').each(function() {
                claimed_qty = activity_claimed_quantity(activity.data('dci'), $(this).data('item-id'));
                $(this).find('[data-dci="'+data[i].dci+'"]').each(function(){
                  if (data[i].qty_rem >= claimed_qty && data[i].qty_rem != 0) {
                    $(this).addClass('visible').slideDown();
                  } else {
                    if (!$(this).find('input').is(':checked')) {
                      $(this).find('input').prop('checked',false);
                      $(this).removeClass('visible').slideUp();
                    }
                  }
                });  
              });      
            }
            activities = $('.item_activities[data-item-id="'+item+'"]').find('.activities');
          } else {
            for (var i=0; i<data.length; i++) {  
              if (data[i].qty_rem >= total_quantity() && data[i].qty_rem != 0) { 
                $('li[data-dci="'+data[i].dci+'"]').addClass('visible').slideDown()
              } else { 
                $('li[data-dci="'+data[i].dci+'"]').find('input').prop('checked',false);
                $('li[data-dci="'+data[i].dci+'"]').removeClass('visible').slideUp();
              }
            }
            activities = $('.activities');
          }
        }

        if (activities) {
          activities.each(function() {
            var open_activities = false
            $(this).find('.modifier_group').each(function() {

              group_name = $(this).attr('data-group');
              if ($(this).next('ul').first().children('li.visible[data-group="'+group_name+'"]').length > 0) {
                $(this).slideDown();
                open_activities = true
              } else {
                hide_modifier_group($(this));
              }
            });

            if (open_activities) {
              $(this).find('.savings_info').slideDown();
              $(this).addClass('open');
            } else {
              $(this).find('.savings_info').slideUp();
              $(this).removeClass('open');
            }
          });
        }

        if (should_get_sw_subtotal()) {
          get_sw_subtotal();
        } else {
          $('.running_total').removeClass('loading').text(calc_subtotal()).formatCurrency();
        }

        quick_checkout_check();
      },
      error: function(data) {
        if (item_activities.length > 0)
          if (dci_codes.length > 0) {
            for (var i = 0; i < dci_codes.length; i++) {
              item_activities.find('li[data-dci="'+dci_codes[i]+'"] input').prop('checked', false);
              item_activities.find('li[data-dci="'+dci_codes[i]+'"] .checking_availability_error').removeClass('hide');
            }
          } else {
            item_activities.siblings(".loading_activities_error").removeClass('hide');
          }
        console.log('Failed to check availability');
      
      },
      complete: function() {

        unlock_buy_area(false);
        // Remove activities loader
        $('.loading_activities').addClass('hide');
        // Remove availability loader
        $('.activities').find('li').removeClass('loading');
        $('.activities').find('.checking_availability').addClass('hide');        
      }
    });
  } // end if quanity 0
}


