function show_order_status_dialog() {
  var dialog = $('#checkout_processing');
  if (!dialog[0]) {
    dialog = $('<div/>', {
      id: "checkout_processing"
    })
  }
  dialog.html('<p class="message">Submitting your order...</p>');
  dialog.appendTo('body').dialog({
    width: 400,
    // height: 200,
    closeOnEscape: false,
    draggable: false,
    open: function(event, ui) { $(".ui-dialog-titlebar-close").hide(); },
    resizable: false,
    modal: true,
    title: "Order Status",
    close: function(){
      dialog.dialog('destroy').remove();
    }
  });
  $('<div/>', {
    "class": "progress_bar"
  }).appendTo('#checkout_processing');

  loadCheckoutSurvey();
}

var poll_tries=0;
function poll_for_checkout_success(sw_id) {
  $.ajax({
    url: '/checkout/'+sw_id+'/get_status',
    type: 'GET',
    dataType: 'json',    
    success: function(data) {
      if (data.status == 'pending') {
        if (poll_tries < 60) {
          poll_tries++;
          setTimeout(function() { poll_for_checkout_success(sw_id); }, 2000);
        } else {
          if (window.survey) {
            checkout_processing_message("There was a delay in processing your payment. We will keep trying and email you when your order has been processed.<br>Order #:"+sw_id, "/account", "Go to My Account page");          
          } else {
            window.location = "/checkout/delayed";
          }
        }
      } else if (data.status == 'error') {
        checkout_processing_message("There was an error processing your order: "+data.message+". Please correct the error and try again.<br>Order #:"+sw_id, "close_dialog", "Close");
      } else if (data.status == 'rejected') {
        checkout_processing_message("We're sorry, your order could not be completed. Please try again or contact guest services.<br>Order #:"+sw_id, "close_dialog", "Close");
      } else {
        if (window.survey) {
          checkout_processing_message("Your order was processed successfully", "/orders/"+data.order_number+"/thanks", "View Receipt");
        } else {
          window.location = "/orders/"+data.order_number+"/thanks";
        }
      }
    },
    error: function(data) {
      checkout_processing_message("There was an error processing your payment. Please try again in a few minutes.<br>Order #:"+sw_id, "close_dialog", "Close");
    }
  });
}

function checkout_processing_message(message, url, link_text) { 
  $(".ui-dialog-titlebar-close").show();
  $('#checkout_processing .message').html(message);
  $('#checkout_processing .progress_bar').remove();
  if (url == "close_dialog") {
    $('<a/>', {
      "href": "#",
      "class": "button",
      text: link_text,
      click: function(){
        $('#checkout_processing').dialog('destroy').remove();
      }
    }).insertAfter('#checkout_processing .message');
  } else {
    $('<a/>', {
      "href": url,
      "class": "button",
      text: link_text
    }).insertAfter('#checkout_processing .message');
  }
}

function checkout_login_issue(message) { 
  $(".ui-dialog-titlebar-close").show();
  $('#checkout_processing .message').html(message);
  $('#checkout_processing .progress_bar').remove();
  $('<a/>', {
    "href": '/login',
    "class": "button login-btn",
    text: 'Login'
  }).insertAfter('#checkout_processing .message');
  $('<a/>', {
    "href": '/forgot',
    "class": "small",
    text: "Forgot your password?"
  }).insertAfter('#checkout_processing .login-btn');
  $('.login-btn').after(" ");
}

function loadCheckoutSurvey() {
  $.ajax({
    url: "/checkout/survey.html",
    dataType: "html",
    success: function(data) {
      window.survey = true

      // resize dialog
      $("#checkout_processing").dialog("option", "width", 620)
      $("#checkout_processing").dialog("option", "height", 520)
      $("#checkout_processing").dialog("option", "position", $("#checkout_processing").dialog("option", "position"));

      // append survey
      var $survey = $('<div/>', {
        "class": "checkout_survey",
        "html": data
      }).appendTo('#checkout_processing');

      $survey.find("form").submit(function(){
        var data = $(this).serialize();

        $.ajax({
          url: $(this).attr('action'),
          data: data,
          type: 'POST',
          dataType: 'json',
          success: function(data) {
            window.survey = false
            $survey.text("Thank you for your response.");
            $("#checkout_processing").dialog("option", "width", 400)
            $("#checkout_processing").dialog("option", "height", 200)
            $("#checkout_processing").dialog("option", "position", $("#checkout_processing").dialog("option", "position"));
          }
        });

        return false;
      });

    }
  });
}
