$.widget("ui.tsSlideshow", {

  // default options
  options: {
    rate: 3000,
    duration: 1500
  },

  _create: function() {
    var self = this;
    self.curIndex = self.element.children().first().index();
    self.element.children().eq( self.curIndex ).fadeIn(self.options.duration, function(){
      self.timer = setTimeout(function(){self.next();},self.options.rate);
    });

  },

  update: function() {

  },

  next: function() {		
    var self = this;
    self.element.children().eq( self.curIndex ).css('z-index','0');
    self.element.children().eq( self._nextIndex() ).css('z-index','1').fadeIn( self.options.duration, function(){			
      // hide previous
      self.element.children().eq( self.curIndex ).css('display','none');

      self.curIndex = self._nextIndex();
      clearTimeout(self.timer);
      self.timer = setTimeout(function(){self.next();},self.options.rate);	

    });
  },

  start: function() {
    var self = this;
    self.next();
  },

  stop: function() {
    var self = this;
    clearTimeout(self.timer);
  },

  _nextIndex: function() {
    var self = this;
    var total = self.element.children().length;
    if ( self.curIndex == (total - 1)) {
      return 0;
    }
    return this.curIndex + 1;
  },

  destroy: function() {
    $.Widget.prototype.destroy.apply(this, arguments); // default destroy
    // now do other stuff particular to this widget
  }

});
