// ===================
// = Notify Function =
// ===================
function notify(message,status) {
  var repeat = false;
  if (!message) { return false; }
  if (!status) { status = "notice"; }

  if ( !$('#ts-notify').length ) { 
    $('<div/>', {
      "id": "ts-notify"
    }).hover(function(){			// stop fadeout on hover
      $(this).stop(true);
      $(this).fadeTo(300,1);
    },function(){							// re-instate fadeout on mouseLeave
      $(this).delay(10000).fadeTo(600,0,function(){ $(this).remove(); });
    }).appendTo('body');	
  }	

  var $close_button = $('<button/>', {
    "class": "close",
    click : function(){
      $(this).parent().slideUp('500',function(){ 
        $(this).remove();
        if ( !$('#ts-notify .notification').length ) {
          $('#ts-notify').remove();
        }
      });
    }
  });

  var notification = $('<div/>', {
    "class": "notification "+status,
    "html": message
  }).prepend($close_button);


  $('#ts-notify > div').each(function(){
    if ( $(this).html() == notification.html() ) { repeat = true; }
  });

  if ( repeat == false ) {
    $('#ts-notify').prepend(notification);

    $('#ts-notify').stop(true).fadeTo(1,1);

    $('#ts-notify').show().fadeTo(600,1,function(){
      //delay and fadeout
      $(this).delay(5000).fadeTo(600,0,function(){$(this).remove();});
    });
  }

}

// ===========================
// = AJAX Response Processor =
// ===========================
function processResponse(response) {
  if (response.message) {
    notify(response.message,response.status);
  }
  
  if ( response.html ) {
    if (response.mode == "replace") {
      $($('[id="'+response.dom_id+'"]')).each(function(){
        $(this).replaceWith(response.html)
      }) 
      $('body').trigger('content_replaced');
    }
    if (response.mode == "insert") {
      $($('[id="'+response.dom_id+'"]')).each(function(){
        $(this).append(response.html);
      }) 
    }
  // Special case: Remove image
  } else if (response.mode == "replace" && response.dom_id.indexOf("container_media") >= 0) {
    $($('[id="'+response.dom_id+'"]')).each(function(){
      $(this).find('img').remove();
    }) 
    $('body').trigger('content_replaced');
  }

  if (response.mode == "remove") {
    $($('[id="'+response.dom_id+'"]')).each(function(){
      $(this).slideUp(600, function(){ $(this).remove(); });
    }) 
  }

  if (response.mode == "redirect") {
    window.location = response.url;
  }

  $('.loading').removeClass("loading");

}

