jQuery.fn.full_screen_bg = function(url,gravity,slideshow,link_url, rate) {
  var $container = $(this);
  if (!gravity) { gravity = "c"; }

  if ( $('#fsbg').length == 0 ) {
    var $bg_wrapper = $('<div/>',{
      "id" : "fsbg",
      "class" : "media full_screen_bg"
    });
  } else {
    var $bg_wrapper = $('#fsbg');	
  }

  $container.prepend($bg_wrapper);

  var img = new Image();
  $(img).load(function() {
    $(this).hide();
    $bg_wrapper.append(this);
    $(this).addClass(gravity);
    if ( link_url ) {
      $(this).click(function(){
        window.location = link_url;
      });
      $(this).css('cursor','pointer');
    }
    // positionBackground();
    if ( slideshow ) {
      if ( !$bg_wrapper.data('tsSlideshow') ) {
        if (!rate) { rate = 3 }
        $bg_wrapper.tsSlideshow({ rate: parseInt(rate) * 1000 } );
      }
    } else {
      $(this).fadeIn(600,function(){
        if (typeof firstVisit == 'function') {
          firstVisit();					
        }
      });
    }		
  }).attr('src', url);

};

