function closeGrid() {
  $('body').removeClass("panel_open");
  $('#nav li').removeClass('active');
  $('.panel').fadeOut(400);
  $('#pullout').animate({
    left: '-762px'
  }, 400, function(){
    $(this).removeClass("open");
    $(this).find('.media .slideshow').cycle('pause');
    resume_full_screen_bg();
  });
}

function openGrid(panel_id) {
	if (!panel_id) { return false; } 
  if (panel_id.match(/itinerary/)) {
    return false;
  }
  try {
    if ( $('#pullout #'+panel_id).length > 0 ) {
      $('body').addClass("panel_open");
      $('.panel').fadeOut(400);
      $('#nav li').removeClass('active');
      $('#nav a[href="#'+panel_id+'"]').closest('li').addClass('active');
      $('#pullout').not('.open').animate({
        left: '236px'
      }, 400, function() {
        $(this).addClass("open");
      });
      $('#pullout #'+panel_id).fadeIn(400, function(){
        $(this).find('div.img').lazyLoadImages();
        $(this).find('.media .slideshow').cycle('resume');
        pause_full_screen_bg();
      });
      
      //_gaq.push(['_trackEvent', 'Grids', panel_id]);
      if (typeof ga !== 'undefined')
        ga('send', 'event', panel_id, 'click', 'grid panels');
    }    
  } catch (e) {
    console.log('Failed to open grid', e);
  }

}

